import mongoose, { Document } from "mongoose";
import jwt from "jsonwebtoken";

interface IUser extends Document {
    name: string,
    email: string,
    password: string,
    profilePhotoUrl?: string,
    role: "admin" | "student",
    getJwt(): string
}

const UserSchema = new mongoose.Schema<IUser> ({
    name: {
        type: String,
        minLength: 1,
        maxLength: 255,
        required: true
    },
    email: {
        type: String,
        unique: true,
        required: true
    },
    password: {
        type: String,
        required: true,
        minLength: 6
    },
    profilePhotoUrl: {
        type: String,
        default: "data:image/jpeg;base64,"
    },
    role: {
        type: String,
        enum: ["admin","student"],
        default: "student",
        required: true
    }
}, { timestamps: true });

UserSchema.methods.getJwt = function(){
    const thisuser = this;
    const token = jwt.sign({_id: thisuser._id},process.env.JWT_SECRET!,{
        expiresIn: '7d'
    });
    return token
}

export const User = mongoose.model('User',UserSchema);